jQuery(document).ready(function($){var canvas1=document.getElementById("buttStock");var canvas2=document.getElementById("ergonomicGrip");var canvas3=document.getElementById("fingerGaurd");var views=[canvas1.getContext("2d"),canvas2.getContext("2d"),canvas3.getContext("2d")];var vertices=[];
var data=[{id:"buttStock",timer:400,x:[25,25,20,30],y:[198,0,1,1]},{id:"ergonomicGrip",timer:400,x:[25,25,20,30],y:[0,198,198,198]},{id:"fingerGaurd",timer:400,x:[25,25,20,30],y:[0,198,200,200]}];var a=1;var points; $('.canvasWrap').mouseenter(function(){var touchpointTracking=1;if(touchpointTracking<2){var getPosition=0;a=1;vertices=[];goLive(data[getPosition]);}
touchpointTracking++;console.log(touchpointTracking);});
function goLive(button){for(m=0;m<views.length;m++){views[m].strokeStyle="#FFFFFF"}
for(var i=0;i<button.x.length;i++){vertices.push({x:button.x[i],y:button.y[i]});}
points=calcWaypoints(vertices);animate();}
function calcWaypoints(vertices){var waypoints=[];for(var i=1;i<vertices.length;i++){var pt0=vertices[i-1];var pt1=vertices[i];var dx=pt1.x-pt0.x;var dy=pt1.y-pt0.y;for(var j=0;j<20;j++){var x=pt0.x+dx*j/20;var y=pt0.y+dy*j/20;waypoints.push({x:x,y:y});}}
return(waypoints);}
function animate(){if(a<points.length-1){requestAnimationFrame(animate);}
 
for(m=0;m<views.length;m++){views[m].beginPath();views[m].moveTo(points[a-1].x,points[a-1].y);views[m].lineTo(points[a].x,points[a].y);views[m].lineWidth=2;views[m].stroke();} 
a++;}
crossbowDetailsActivate();crossbowDetailsClickActions();function crossbowDetailsActivate(){$('.crossbowDetails .item').mouseenter(function(){$(this).find('img').attr('src','//s7d2.scene7.com/is/image/dksfed/detailsPlusHover?fmt=png-alpha');});$('.crossbowDetails .item').mouseleave(function(){$(this).find('img').attr('src','//s7d2.scene7.com/is/image/dksfed/detailsPlus?fmt=png-alpha');});}
function crossbowDetailsClickActions(){$('.crossbowDetails .item').click(function(){$(this).siblings('.item').toggleClass('desk-one-eighth closedItems desk-one-third');$(this).toggleClass('desk-three-quarters desk-one-third active');$(this).siblings('.item').removeClass('active');$(this).removeClass('.desk-one-eighth');});
}});